mapper.select:
SELECT RECEIVER_ID receiverId,RECEIVER_NICK receiverNick, To_char(send_date, 'YYYY-MM-DD') sendDate , To_char(send_date, 'hh:mi:ss') sendTime, SENDER_NICK senderNick,CONTENT content,SENDER_DEL_FLAG senderDelFlag, RECIPIENT_DEL_FLAG recipientDelFlag,SEND_DATE sendDate,MESSAGE_ID messageId,READ_STATE readState FROM ( SELECT * FROM ( select ROW_NUMBER() OVER(ORDER BY MESSAGE_ID DESC) AS RW, RECEIVER_ID,NICKNAME RECEIVER_NICK,SENDER_ID, SENDER_NICK,CONTENT,SENDER_DEL_FLAG,RECIPIENT_DEL_FLAG, SEND_DATE,MESSAGE_ID,READ_STATE from message_tb_view WHERE
SENDER_ID = #{userId} AND SENDER_DEL_FLAG = 0
RECEIVER_ID = #{userId} AND RECIPIENT_DEL_FLAG =0
AND READ_STATE = 1
AND READ_STATE = 0
) message_tb_view WHERE RW <= #{lastNum} ) WHERE RW >= #{firstNum}
